// Declare modules for importing video files
declare module '*.mp4' {
  const src: string;
  export default src;
}
declare module '*.pdf' {
  const src: string;
  export default src;
}

// You can add more declarations for other file types as needed
declare module '*.jpg' {
  const src: string;
  export default src;
}

declare module '*.png' {
  const src: string;
  export default src;
}

// Example for other media types
declare module '*.wav' {
  const src: string;
  export default src;
}